<template>
  <div id="home">
    <div class="main">
      <div class="list">
        <div class="list-box center" v-for="(item,i) in imgList" :key="i">
          <a class="list-item am" @click="getFile(item)">
            <img :class="mobile?'':'img'" :src="item.data" />
            {{item.text}}
          </a>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      imgList: [
        {
          text: "图片",
          cat: "image",
          data:
            "data:image/png;base64,"
        },
        {
          text: "视频",
          cat: "video",
          data:
            "data:image/png;base64,"
        },
        {
          text: "音频",
          cat: "audio",
          data:
            "data:image/png;base64,"
        },
        {
          text: "压缩",
          cat: "compress",
          data:
            "data:image/png;base64,"
        },
        {
          text: "文档",
          cat: "text",
          data:
            "data:image/png;base64,"
        },
        {
          text: "其它",
          cat: "other",
          data:
            "data:image/png;base64,"
        },
        {
          text: "上传",
          cat: "upload",
          data:
            "data:image/png;base64,"
        }
      ],
      mobile: false
    };
  },
  created() {
    if (/(iPhone|iPad|iPod|iOS|Android)/i.test(navigator.userAgent)) {
      this.mobile = true;
    }
  },
  methods: {
    getFile(item) {
      if (item.cat === "upload") {
        this.$router.push({
          name: "upload"
        });
        return;
      }
      this.$router.push({
        name: "content",
        params: { cat: item.cat, item }
      });
    }
  }
};
</script>

