#home {
  width: 100%;
  height: 100%;
  .main {
    .list {
      display: flex;
      flex-wrap: wrap;
      .list-box {
        width: 33.333vw;
        .list-item {
          width: 20vw;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
          margin: 10vw 0;
          img {
            margin-bottom: 8px;
          }
        }
      }
    }
  }
}

.img {
  width: 100%;
  max-width: 80px;
  transition: all 0.4s ease 0s;
}
